mapper.update:
UPDATE users
SET status = 2
WHERE id = #{id}

UPDATE votes SET
title = #{title}
WHERE id = #{id}

UPDATE votes SET
password = #{password}
WHERE id = #{id}

UPDATE votes SET
participator_limit = #{participatorLimit}
WHERE id = #{id}

UPDATE votes SET
participator_num = participator_num + 1
WHERE id = #{id}

UPDATE votes SET
is_deleted = TRUE
WHERE id = #{id}

mapper.select:
SELECT participator_limit
FROM votes
WHERE id = #{id}

SELECT visibility_limit
FROM votes
WHERE id = #{id}

SELECT anonymous
FROM votes
WHERE id = #{id}

SELECT creator_id
FROM votes
WHERE id = #{voteId}

SELECT start_time
FROM votes
WHERE id = #{id}

SELECT end_time
FROM votes
WHERE id = #{id}

mapper.update:
UPDATE votes
SET qa_path = #{qa_path}
WHERE id = #{id}

UPDATE votes
SET visibility_limit = ! visibility_limit
WHERE id = #{id}

UPDATE votes
SET
    title =#{title},
    start_time = #{startTime},
    end_time = #{endTime}
WHERE id = #{id}

update problem_options set
title=#{title}
WHERE vote_id = #{voteId} AND id = #{id}
