mapper.update:
UPDATE users
SET status = 2
WHERE id = #{id}

mapper.select:
SELECT votes.id,votes.title,start_time,end_time,votes.updated_at,mobile,visibility_limit,max,type
FROM votes,users

    votes.is_deleted = 0
    AND
    votes.creator_id = users.id
    AND title LIKE  CONCAT('%',#{title},'%')
     AND votes.created_at > #{createTimeFrom}
    AND votes.created_at < #{createTimeTo}
     AND start_time > #{startTimeFrom}
     AND start_time < #{startTimeTo}
    AND visibility_limit = #{visibility}
    AND mobile LIKE CONCAT('%',#{mobile},'%') 

        AND #{now} < start_time


        AND #{now} > start_time AND #{now} < end_time


        AND #{now} > end_time


ORDER BY votes.id DESC

SELECT title
FROM activity
WHERE id = #{id}

